import { NodeTypeOption } from "three/examples/jsm/nodes/core/constants.js";
import UniformNode from "three/examples/jsm/nodes/core/UniformNode.js";
import { NodeOrType, ShaderNodeObject } from "three/examples/jsm/nodes/shadernode/ShaderNode.js";

export default class BufferNode extends UniformNode<ArrayLike<number> | null> {
    isBufferNode: true;

    bufferType: string;
    bufferCount: number;

    constructor(value: ArrayLike<number> | null, bufferType: NodeTypeOption, bufferCount?: number);
}

export const buffer: (
    value: ArrayLike<number> | null,
    nodeOrType: NodeOrType,
    count: number,
) => ShaderNodeObject<BufferNode>;
