import { NodeTypeOption } from "three/examples/jsm/nodes/core/constants.js";
import { NodeOrType, NodeRepresentation, ShaderNodeObject } from "three/examples/jsm/nodes/shadernode/ShaderNode.js";
import StorageArrayElementNode from "three/examples/jsm/nodes/utils/StoargeArrayElementNode.js";
import BufferNode from "three/examples/jsm/nodes/accessors/BufferNode.js";

export default class StorageBufferNode extends BufferNode {
    readonly isStorageBufferNode: true;
    bufferObject: boolean;

    constructor(value: ArrayLike<number>, bufferType: NodeTypeOption, bufferCount?: number);

    element(indexNode: NodeRepresentation): ShaderNodeObject<StorageArrayElementNode>;

    setBufferObject(value: boolean): this;
}

export const storage: (
    value: ArrayLike<number>,
    nodeOrType: NodeOrType,
    count: number,
) => ShaderNodeObject<StorageBufferNode>;
export const storageObject: (
    value: ArrayLike<number>,
    nodeOrType: NodeOrType,
    count: number,
) => ShaderNodeObject<StorageBufferNode>;
